mapper.select:
SELECT * FROM t_user
WHERE email= #{email}
AND authCd IS NULL

mapper.update:
UPDATE t_user SET
authCd = null
WHERE email=#{email} AND authCd = #{authCd}

UPDATE t_user


        mainProfile = #{mainProfile}


        pw = #{pw}


        nm = #{nm}


        tel = #{tel}


WHERE iuser= #{iuser}
